
public class OperEx7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//부정연산자
		boolean power = false;
		System.out.println(power);
		
		power = !power;
		System.out.println(power);
		
		power = !power;
		System.out.println(power);
		
		boolean ori = true;
		System.out.println(ori);
		ori = !ori;
		System.out.println(ori);
		ori = !ori;
		System.out.println(ori);
	}

}
