
public class OneToOneHundred {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num = 0;
		num = 1;
		int sum = 0;
		sum = sum + (num + 1);
		
		while (num <= 10) {
			System.out.println(num  + " + " + sum +  " = " + (num + sum));
			num++;
			sum++;
		}
		
		
		
	}

}
